  .then(azureADIssuer => {
    const client = new azureADIssuer.Client({
      client_id: 'your-client-id',
      client_secret: 'your-client-secret',
      redirect_uris: ['your-redirect-uri'],
      response_types: ['code'],
    });

    // Generate a code verifier and challenge
    const code_verifier = generators.codeVerifier();
    const code_challenge = generators.codeChallenge(code_verifier);

    // Authorization URL
    const authorizationUrl = client.authorizationUrl({
      scope: 'openid profile email offline_access https://graph.microsoft.com/.default',
      code_challenge,
      code_challenge_method: 'S256',
    });

    console.log('Visit this URL to authorize:', authorizationUrl);

    // After user authorizes, handle the callback
    // Assuming you have the authorization code from the callback
    const params = client.callbackParams(req);
    client.callback('your-redirect-uri', params, { code_verifier })
      .then(tokenSet => {
        console.log('Access Token:', tokenSet.access_token);

        // Use the access token to call Microsoft Graph API
        // Example: Get user profile
        fetch('https://graph.microsoft.com/v1.0/me', {
          headers: {
            Authorization: `Bearer ${tokenSet.access_token}`,
          },
        })
          .then(response => response.json())
          .then(data => console.log(data));
